mapper.select:

select b.*            
from(
    select rownum as rm, a.*            
    from(
        select *
        from notice 
        where noticeState != '2' 
        order by noticeNum desc
    ) a
) b
where b.rm>=#{startRow} AND b.rm<=#{endRow}

mapper.update:
UPDATE notice
SET noticeReadCount = noticeReadCount + 1
WHERE noticeNum=#{noticeNum}

mapper.select:
SELECT b.*
FROM notice b
WHERE noticeNum=#{noticeNum}

mapper.update:
UPDATE notice
SET noticeTitle=#{noticeTitle}, noticeContent=#{noticeContent}
WHERE noticeNum=#{noticeNum}

mapper.select:
SELECT d.* FROM(SELECT rownum as rm, c.* FROM(SELECT b.* FROM(SELECT a.* FROM(SELECT * FROM notice ORDER BY noticeNum DESC )a
where a.noticeState = '1'
where a.noticeState = '2'
)b
AND b.noticeTitle LIKE '%'||#{searchWord}||'%'
AND b.noticeContent LIKE '%'||#{searchWord}||'%'
AND (b.noticeContent LIKE '%'||#{searchWord}||'%' OR b.noticeTitle LIKE '%'||#{searchWord}||'%')
)c )d WHERE d.rm BETWEEN #{startRow} AND #{endRow}
